import 'dart:convert';

import '../../interfaces/pdf_interface.dart';
import '../io/pdf_constants.dart';
import '../primitives/pdf_dictionary.dart';
import '../primitives/pdf_name.dart';
import '../primitives/pdf_number.dart';
import '../primitives/pdf_stream.dart';

/// Class represents the ICC Colorspace.
//Used during creation of documents with PDF/A1B compliance.
class PdfICCColorProfile implements IPdfWrapper {
  /// Initializes a new instance of the [PdfICCColorProfile] class.
  PdfICCColorProfile() : super() {
    stream.compress = true;
    stream.setProperty(PdfDictionaryProperties.filter,
        PdfName(PdfDictionaryProperties.flateDecode));
    stream.setProperty(PdfDictionaryProperties.n, PdfNumber(3));
    stream.beginSave = _beginSaveStream;
  }

  /// internal field
  final PdfStream stream = PdfStream();

  /// internal field
  final String profileData =
      '';

  //Implementation.
  //Handles the BeginSave event of the Stream control.
  void _beginSaveStream(Object sender, SavePdfPrimitiveArgs? args) {
    stream.clearStream();
    stream.dataStream = base64.decode(profileData).toList();
  }

  /// internal property
  IPdfPrimitive? get element => stream;

  set element(IPdfPrimitive? value) {
    throw ArgumentError("Primitive element can't be set");
  }
}
